import Image from 'next/image';

interface BrandLogoProps {
  src: string;
  alt: string;
  isSquare: boolean;
  squareSize?: number;
  className?: string;
  priority?: boolean;
  sizes?: string;
}

export default function BrandLogo({
  src,
  alt,
  isSquare,
  squareSize = 40,
  className = "",
  priority = false,
  sizes
}: BrandLogoProps) {
  if (isSquare) {
    return (
      <Image
        src={src}
        alt={alt}
        width={squareSize}
        height={squareSize}
        className={`rounded-lg ${className}`}
        priority={priority}
        sizes={sizes || `${squareSize}px`}
        placeholder="blur"
        blurDataURL="data:image/jpeg;base64,"
      />
    );
  }

  // Rectangular logos (Modern brands)
  return (
    <Image
      src={src}
      alt={alt}
      width={300}
      height={80}
      className={`h-8 w-auto object-contain ${className}`}
      priority={priority}
      sizes={sizes || "300px"}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  );
}
